mapper.select:
select s.*,c.id from student s left join clazz c on s.clazz_id=c.id


        s.name like concat ('%',#{name},'%')


        and s.degree=#{degree}


        and c.clazz_id=#{clazzId}

mapper.update:
update student


        violation_score=violation_score+#{score},

    violation_count=violation_count+1,
    update_time=#{updateTime}

where id=#{id}
